import React, { Component } from 'react';
import InputTodo from './InputTodo';
import Item from './Item';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      listTodo: [],
      isRemoveBtnDisabled: true,
    };

    this.addTodo = this.addTodo.bind(this);
    this.handleSelectBtn = this.handleSelectBtn.bind(this);

  }

  handleSelectBtn() {
    this.setState({ isRemoveBtnDisabled: false });
  }

  addTodo(todo) {
    this.setState((state) => ({ listTodo: [...state.listTodo, todo] }));
  }

  render() {
    const { listTodo, isRemoveBtnDisabled } = this.state;
    return (
      <main className="App">
        <InputTodo addTodo={ (todo) => this.addTodo(todo) } />
        <ul>
          { listTodo.map((todo, index) => (
            <li key={ index + 1 }>
              <Item
                content={ todo }
                handleSelectBtn={ this.handleSelectBtn }
                isDisabled={ isRemoveBtnDisabled }
                handleRemoveBtn={this.handleRemoveBtn}
              />
            </li>
          )) }
        </ul>
      </main>
    );
  }
}
export default App;
